import { Router } from "express";

const router = Router();

router.get("/", (req, res) => {
  res.render("index");
});

router.post("/tasks/add", (req, res) => {
  res.send("Guardado");
});

router.get("/about", (req, res) => {
  res.render("about");
});

router.get("/edit", (req, res) => {
  res.render("edit");
});

// Exportar para poder usar desde otro archivo
export default router;

