/* realizar la conexión a la base de datos */


( async () => {
    try {
        const db = await connect("mongodb://localhost/crud-mongo");
        console.log("Base de datos", db.connection.name, "conectada");
    } catch( error ) {
        console.error( error.message );
    } 
})();

