import Task from "../models/Task";

export const renderTaks = async (req, res) => {
  try {
    console.log("Aquí");
    //const tasks = await Task.find().lean();
   // res.render("index", { tasks: tasks });
  } catch (error) {
    console.error(error.message);
  }
};

