sourcesContent:
import { Schema, model } from "mongoose";

// definimos los datos a almacenar - esquema
const taskSchema = new Schema(
  {
    title: {
      type: String,
      required: true,
      unique: true,
      trim: true,
    },
    description: {
      type: String,
      required: true,
    },
    done: {
      type: Boolean,
      default: false,
    },
  },
  {
    timestamps: true,
    versionKey: false,
  }
);

// Exportamos la función
export default model("Task", taskSchema);

